INSERT INTO `main_site_contact` (`id`, `ime_i_prezime`, `email`, `tekst`, `datum`) VALUES
(1, 'Marko Lalic', 'marko.lalic@gmail.com', 'Test kontakt.', '2012-10-03 02:02:41'),
(2, 'Vanja', 'vanjapancic@gmail.com', 'Gdje si Lalke šaaaa imaaaaa :D', '2012-10-03 09:34:51'),
(3, 'Selma Begic', 'selma.begic@stu.ssst.edu.ba', 'Danas sam poslala prijavu za SSA, međutim tek sam naknadno primjetila da se rubrika u vezi poznavanja jezika ondosila ja stepen poznavanja engleskog jezika, a ne općenito poznavanje jezika, pa se nadam da to nije problem. Imam visok stepen poznavanja engleskog jezika, uzevši u obzir i činjenicu da studiram na engleskoom jeziku. Nadam se da ćete uzeti u obzir ovu malu ispravku.\r\n\r\nIzvinjavam se na smetnji i hvala unaprijed.', '2012-10-04 13:33:27'),
(4, 'Medić Marijana', 'medicmajamarijana@hotmail.com', 'Zanima me da li moramo oba dana prisustvovati na sva četiri termina,ili samo one koje mi želimo slušati i koji nas zanimaju? I da li ima obezbjeđeno prenoćište s obzirom da neki studenti nisu u Sarajevu i ako se mora oba dana prisustvovati.', '2012-10-08 19:02:18'),
(5, 'Jasmina Dževlan', 'jasmina_dzevlan@hotmail.com', 'Interesuje me kada bi mogla ocekivati rezultate o tome koce učestvovati u projektu?  :D', '2012-10-08 20:19:08'),
(6, 'Jana Torbica', 'jana.torbica@bih.net.ba', 'Postovani,\r\n\r\nMoje ime je Jana Torbica, imam 18 godina i zavrsna sam godina IB Diploma programa u Sarajevu. \r\n\r\nZainteresovana sam za ucesce na treninzima koje organizujete ali posto nisam student interesuje me da li mogu da se registrujem.\r\n\r\nS obzirom na aktivnosti kojima se inace bavim i na moja interesovanja (psihologija, novinarstvo), jako bi mi znacilo da prisustvujem pomenutim treninzima.\r\n\r\nVolonterski sam aktivna u organizaciji Narko NE i aktivno ucestvujem u implementaciji projekta Balkan Stringers (www.balkanstringers.com) a tokom ovog ljeta imala sam priliku da kao jedan od cetiri predstavnika BiH ucestvujem u programu YASI (Youth Ambassadors Summer Institute www.yasinstitute.org) u Briselu.\r\n\r\nSrdacan pozdrav\r\nJana Torbica\r\n\r\nkontakt: 066-711-799', '2012-10-11 09:22:47'),
(7, 'ervin', 'ervin.porca@gmail.com', 'postovani\r\ndanas sam vidio Vas oglas na ekonomskom fakultetu za sofr skills academy.\r\ninteresuje me kad ce se ponovo moci prijaviti s obzirom da sam na ovaj termin zakasnio.  pozdrav Ervin', '2012-10-15 19:12:36'),
(8, 'LeAAxeVCeMe', 'pnnjrv@koyzsr.com', 'yms4Ux  <a href=\"http://eirmbfqdfgmv.com/\">eirmbfqdfgmv</a>, [url=http://euhmpcwzghld.com/]euhmpcwzghld[/url], [link=http://teqketfjdsfm.com/]teqketfjdsfm[/link], http://jcceneqjrbug.com/', '2013-09-16 03:21:00'),
(9, 'Nedima Džaferagić', 'neda.nedima.dzaferagic@gmail.com', 'Poštovani, \r\nbila sam na SSA prošle godine, i bilo je odlično. Vidim da ove godine ima veći broj radionica, pa me zanima da li je ok da se opet prijavimo? \r\nHvala', '2013-10-02 20:57:05'),
(10, 'Selma Spahić', 'spahic.selma@hotmail.com', 'Poštovani,\r\n\r\nObzirom da sam prošle godine učestvovala na prvoj Soft Skills Academy, interesuje me da li postoji mogućnost ponovnog prijavljivanja? Prezadovoljna kompletnom prošlogodišnjom organizacijom, jednim divnim iskustvom, i nevjerovatnim utiscima, željela bih kada bi i ove godina mogla dobiti pravo učešća? Hvala.\r\n\r\nLijep pozdrav,\r\n\r\nSelma Spahić.', '2013-10-02 23:29:59'),
